import React, {Component} from 'react';
import {withAuthorization} from "../Session";
import {NavBar, Button, Icon, List, TextareaItem} from 'antd-mobile'
class CreatePost extends Component {
    state = {
        content:'',
        pics:[]

    }
    contentRef =  React.createRef();
    add = ()=>{
        let {addPost} = this.props
        let content = this.contentRef.current.value;
        const postObj = {id:4,userName:'Jack',time:'10:31 10/10/2019',content: content, pics:[], likes:0}
        addPost(postObj)
        this.setState({
            content:'',
            pics:[]
        })

    }
    onChange = event => {
        this.setState({ content: event });
    };
    render() {

        const {onAddCanceledHandler} = this.props
        let {content,pics} = this.state
        return (
            <div>
                <NavBar
                    mode="light"
                    icon={<Icon type="cross-circle" />}
                    onLeftClick={() => {this.props.history.goBack()}}
                    rightContent={
                        <Icon key="0" type="check-circle-o"  />

                    }
                >Text</NavBar>
                <List>
                    <TextareaItem

                        placeholder={"What's happening?"}
                        autoHeight
                        labelNumber={5}
                        onChange={this.onChange}
                        rows={5}
                        count={200}
                    />
                </List>
                 <div>pictures</div>
                    <div>
                        <button>add pics</button>
                        <button>show location?</button>
                    </div>
                </div>

        );
    }
}
export default CreatePost;
