/* eslint no-dupe-keys: 0 */
import { ListView, NavBar, Button, Flex, Toast, Slider, Icon, ImagePicker } from 'antd-mobile';
import React from 'react'
import ReactDOM from 'react-dom'
import {Link} from 'react-router-dom'

import * as ROUTES from "../../../constants/routes";
import add from "../../icons/add.png";
import { get_all_post, get_all_post_by_uid } from "../../Firebase/getPosts";
import { get_location } from "../../utils/getLocation";
import { withAuthentication } from "../../Session";
// let data = [
//     {
//         img: 'https://zos.alipayobjects.com/rmsportal/dKbkpPXKfvZzWCM.png',
//         title: 'Meet hotel',
//         des: '不是所有的兼职汪都需要风吹日晒',
//         time: '11/12/2019',
//         likes:5,
//         id:1,
//         comments:[],
//         location:''
//     },
//     {
//         img: 'https://zos.alipayobjects.com/rmsportal/XmwCzSeJiqpkuMB.png',
//         title: 'McDonald\'s invites you',
//         des: '不是所有的兼职汪都需要风吹日晒',
//         time: '11/12/2019',
//         likes:5,
//         id:2,
//         comments:[],
//         location:'New Zealand'
//     },
//     {
//         img: 'https://zos.alipayobjects.com/rmsportal/hfVtzEhPzTUewPm.png',
//         title: 'Eat the week',
//         des: '不是所有的兼职汪都需要风吹日晒',
//         time: '11/12/2019',
//         likes:5,
//         id:3,
//         comments:[],
//         location:''
//     },
// ];

let pageIndex = 0;
const NUM_ROWS = 10;

function genData(pIndex = 0) {
    const dataBlob = {};
    for (let i = 0; i < NUM_ROWS; i++) {
        const ii = (pIndex * NUM_ROWS) + i;
        dataBlob[`${ii}`] = `row - ${ii}`;
    }
    return dataBlob;
}

class PostPage extends React.Component {
    constructor(props) {
        super(props);
        const getSectionData = (dataBlob, sectionID) => dataBlob[sectionID];
        const getRowData = (dataBlob, sectionID, rowID) => dataBlob[rowID];

        const dataSource = new ListView.DataSource({
            getRowData,
            getSectionHeaderData: getSectionData,
            rowHasChanged: (row1, row2) => row1 !== row2,
            sectionHeaderHasChanged: (s1, s2) => s1 !== s2,
        });

        this.state = {
            dataSource,
            isLoading: true,
            height: document.documentElement.clientHeight ,
            distance:200,
            data:[{
        img: 'https://zos.alipayobjects.com/rmsportal/hfVtzEhPzTUewPm.png',
        title: 'Eat the week',
        des: '不是所有的兼职汪都需要风吹日晒',
        time: '11/12/2019',
        likes:5,
        id:3,
        comments:[],
        location:'ha'
    }]
        };
    }

    componentDidMount() {

        const callback = (list)=>{
           this.setState({
               data:list
           })

                this.rData = genData();
                this.setState({
                    dataSource: this.state.dataSource.cloneWithRows(this.rData),
                    isLoading: false,
                });

        }
        //console.log(a)

        get_all_post(this.props.firebase,callback);
       // console.log(this.props.firebase.db.collection("posts").get())
        // you can scroll to the specified position
        // setTimeout(() => this.lv.scrollTo(0, 120), 800);

        // simulate initial Ajax

    }

    onEndReached = (event) => {
        // load new data
        // hasMore: from backend data, indicates whether it is the last page, here is false
        if (this.state.isLoading && !this.state.hasMore) {
            return;
        }
        console.log('reach end', event);
        this.setState({ isLoading: true });
        setTimeout(() => {
            this.rData = { ...this.rData, ...genData(++pageIndex) };
            this.setState({
                dataSource: this.state.dataSource.cloneWithRows(this.rData),
                isLoading: false,
            });
        }, 1000);
    }

onClickComment =(id)=>{

    this.props.history.push(`/home/comments/${id}`)
}
slider =(e)=>{
        console.log(e)
      this.setState({
          distance:e
      })
}
likes = ()=>{
    get_location()
}
    render() {
        if(this.state.isLoading){
            Toast.loading('Loading...', 0.5, () => {
                console.log('Load complete !!!');
            });
        }
        const separator = (sectionID, rowID) => (
            <div
                key={`${sectionID}-${rowID}`}
                style={{
                    backgroundColor: '#F5F5F9',
                    height: 8,
                    borderTop: '1px solid #ECECED',
                    borderBottom: '1px solid #ECECED',
                }}
            />
        );
        let index = this.state.data.length - 1;
        const row = (rowData, sectionID, rowID) => {
            if (index < 0) {
                index = this.state.data.length - 1;
            }
            const obj = this.state.data[index--];
            return (


                <div key={rowID} style={{ padding: '0 15px' }}>
                    {rowID==0?  <div style={{height:'70px' ,background:'white'}}></div>:<div/> /*show the 1st post completely*/}
                    <div
                        style={{
                            lineHeight: '40px',
                            color: '#888',
                            fontSize: 15,
                            borderBottom: '1px solid #F6F6F6',
                        }}
                    >{obj.location}</div>
                    <div>
                        <Flex style={{  padding: '15px 0' }}>
                            <img style={{ height: '64px', marginRight: '15px' }} src={obj.userAvatar} alt="" />
                            <div style={{ lineHeight: 1 }}>

                                <div><span style={{  fontSize: '20px',color: '#4e77a1',fontWeight: 'bold' }}>{obj.username}</span></div>
                                <div style={{ color: '#5396a5',fontSize: '18px',marginBottom: '8px',marginTop: '5px'  }}>{obj.time}</div>
                            </div>
                        </Flex>
                        <Flex>
                            <div style={{ marginBottom: '8px', fontWeight: 'bold' }}>{obj.content}</div>
                        </Flex>
                        <div>
                            <ImagePicker
                                files={obj.pictures_url.map((item)=>{
                                    return {url:item}
                                })}
                                onChange={this.onChange}
                                onImageClick={(index, fs) => console.log(index, fs)}
                                selectable={false}
                                disableDelete={true}
                                length={3}

                            />

                        </div>
                    </div>

                   <Flex>
                       <Flex.Item><Button size='small' style={{background:'#5396a5' ,color:"#ecfcee",fontWeight: 'bold'}} onClick={()=> this.onClickComment(obj.postId)}>Comment</Button></Flex.Item>
                   <Flex.Item><Button size='small' style={{background:'#5396a5',color:"#ecfcee",fontWeight: 'bold'}} onClick={this.likes}>Likes {!!!obj.likes?'('+obj.likes+')':""}</Button></Flex.Item>
                   </Flex>
                </div>
            );
        };
        return (
            <div >
                <NavBar
                    style={{backgroundColor: '#5396a5', borderBottomLeftRadius:'100%', borderBottomRightRadius:'100%',  height:"70px",position:'fixed',width:"100%", top:0,zIndex:2}}
                    mode="light"

                    rightContent={
                        <div onClick={()=>this.props.history.push(ROUTES.CREATEPOST)} style={{width: '40px',marginTop:"-20px",}}>
                            <img src={add} style={{width:"50px"}} alt="add"/>
                        </div>

                    }
                ><h1 style={{color:"white",}}>CIRKO</h1>
                </NavBar>
                <Flex   style={{ background: "white",height:"70px",position:'fixed',width:"100%", top:"30px",zIndex:1}}>
                    <Flex.Item style={{flex:'8'}}> <Slider
                        style={{marginLeft:"20px",marginTop:'33px'}}
                    defaultValue={200}
                    min={0}
                    max={10000}
                    step={100}
                    value={this.state.distance}
                    onChange={(e)=>this.slider(e)}

                />

                </Flex.Item>
                    <Flex.Item style={{flex:'1', marginLeft:'1vw',marginTop:'30px' ,fontSize:"20px"} }>
                        {this.state.distance}
                    </Flex.Item>
                    <Flex.Item >
                       <Icon size='small' style={{flex:'1',borderRadius:'100%',background:"lightgrey",marginTop:'3vh', textAlign:'center',width:'30px' ,fontSize:"4vh"} }type={'check'} />
                    </Flex.Item>
                </Flex>
            <ListView
                ref={el => this.lv = el}
                dataSource={this.state.dataSource}

                renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                    {this.state.isLoading ? 'Loading...' : 'Loaded'}
                </div>)}

                renderRow={row}
                renderSeparator={separator}

                style={{
                    height: this.state.height,
                    overflow: 'auto',
                }}
                className="am-list"
                pageSize={4}
                onScroll={() => { console.log('scroll'); }}
                scrollRenderAheadDistance={500}
                onEndReached={this.onEndReached}
                onEndReachedThreshold={10}
            />
            </div>
        );
    }
}

