import React from 'react';
import {withAuthorization} from '../Session';
import Item from "../Item";
import * as ROUTES from "../../constants/routes";
import CreatePost from '../CreatePosts'
import Navigation from "../Navigation";
import PostPage from '../PostPage'
import { TabBar, ListView } from 'antd-mobile';
import './index.css'
import AccountPage from "../Account";
class HomePage extends React.Component {

    state = {
        posts: [{
            id: '1',
            userName: 'Nick',
            time: '10/01/2019',
            comments: [{id:1,userName:'Nick',time:'10:30 10/10/2019',content:"abc", likes:5}],
            content: "null",
            likes: 5,
            location: 'Irvine, CA',
            pics: [{
                imgId: 1,
                imgUrl: "https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=1658603858,686533857&fm=26&gp=0.jpg"
            }]
        },
            {
                id: '2',
                userName: 'Tony',
                time: '10/01/2019',
                comments: ['i like it', "i hate it"],
                content: "null",
                likes: 5,
                location: 'Irvine, CA',
                pics: [{
                    imgId: 1,
                    imgUrl: "https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=1658603858,686533857&fm=26&gp=0.jpg"
                }]
            }],
        isAddClicked: false
    }
    toCommentPage = () => {
        this.props.history.push(ROUTES.COMMENTS);
    }
    onAddClickHandler = () => {
        this.setState({isAddClicked: true})
    }
    onAddCanceledHandler = () => {
        this.setState({isAddClicked: false})
    }
    addPost = (postObj) => {
        let posts = [...this.state.posts]
        posts.unshift(postObj);
        this.setState({posts});
    }

    // render() {
    //     const {isAddClick, posts} = this.state;
    //     return (
    //         <div>
    //             <h1>Home Page</h1>
    //             <button onClick={this.onAddClickHandler}>add</button>
    //             <p>The Home Page is accessible by every signed in user.</p>
    //             <div>
    //                 {posts.map((item) => {
    //                     return (<Item toCommentPage={this.toCommentPage} key={item.id} {...item}/>)
    //                 })
    //                 }
    //             </div>
    //             <div style={{display: isAddClick ? 'block' : 'none'}}>
    //                 <CreatePost addPost={this.addPost} onAddCandeledHandler={this.onAddCanceledHandler}/>
    //             </div>
    //             <Navigation />
    //         </div>
    //     )
    // }

    renderContent(pageText) {
        return (
            <div style={{ backgroundColor: 'white', height: '100%', textAlign: 'center' }}>
                <div style={{ paddingTop: 60 }}>Clicked “{pageText}” tab， show “{pageText}” information</div>
                <a style={{ display: 'block', marginTop: 40, marginBottom: 20, color: '#108ee9' }}
                   onClick={(e) => {
                       e.preventDefault();
                       this.setState({
                           hidden: !this.state.hidden,
                       });
                   }}
                >
                    Click to show/hide tab-bar
                </a>
            </div>
        );
    }

    render() {
        return (
            <div style={{ position: 'fixed', height: '100%', width: '100%', top: 0 }}>
                <ListView
                    ref={el => this.lv = el}
                    dataSource={this.state.dataSource}
                    renderHeader={() => <span>CIRKO</span>}
                    renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                        {this.state.isLoading ? 'Loading...' : 'Loaded'}
                    </div>)}
                    renderSectionHeader={sectionData => (
                        <div>{`Task ${sectionData.split(' ')[1]}`}</div>
                    )}
                    renderRow={row}
                    renderSeparator={separator}
                    style={{
                        height: this.state.height,
                        overflow: 'auto',
                    }}
                    pageSize={4}
                    onScroll={() => { console.log('scroll'); }}
                    scrollRenderAheadDistance={500}
                    onEndReached={this.onEndReached}
                    onEndReachedThreshold={10}
                />
                <TabBar
                    unselectedTintColor="#949494"
                    tintColor="#33A3F4"
                    barTintColor="white"
                    tabBarPosition="bottom"
                    hidden={this.state.hidden}
                    prerenderingSiblingsNumber={0}
                >
                    <TabBar.Item
                        title="Cirko"
                        key="Cirko"
                        icon={<div style={{
                            width: '22px',
                            height: '22px',
                            background: 'url(https://image.flaticon.com/icons/png/128/148/148845.png) center center /  21px 21px no-repeat' }}
                        />
                        }
                        selectedIcon={<div style={{
                            width: '22px',
                            height: '22px',
                            background: 'url(https://image.flaticon.com/icons/png/128/148/148845.png) center center /  21px 21px no-repeat' }}
                        />
                        }
                        selected={this.state.selectedTab === 'blueTab'}
                        badge={1}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'blueTab',
                            });
                        }}
                        data-seed="logId"
                    >
                        <PostPage />
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://gw.alipayobjects.com/zos/rmsportal/BTSsmHkPsQSPTktcXyTV.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        selectedIcon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://gw.alipayobjects.com/zos/rmsportal/ekLecvKBnRazVLXbWOnE.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        title="Koubei"
                        key="Koubei"
                        badge={'new'}
                        selected={this.state.selectedTab === 'redTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'redTab',
                            });
                        }}
                        data-seed="logId1"
                    >
                        {this.renderContent('Koubei')}
                    </TabBar.Item>
                    <TabBar.Item
                        icon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://zos.alipayobjects.com/rmsportal/psUFoAMjkCcjqtUCNPxB.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        selectedIcon={
                            <div style={{
                                width: '22px',
                                height: '22px',
                                background: 'url(https://zos.alipayobjects.com/rmsportal/IIRLrXXrFAhXVdhMWgUI.svg) center center /  21px 21px no-repeat' }}
                            />
                        }
                        title="Friend"
                        key="Friend"
                        dot
                        selected={this.state.selectedTab === 'greenTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'greenTab',
                            });
                        }}
                    >
                        {this.renderContent('Friend')}
                    </TabBar.Item>
                    <TabBar.Item
                        icon={{ uri: 'https://zos.alipayobjects.com/rmsportal/asJMfBrNqpMMlVpeInPQ.svg' }}
                        selectedIcon={{ uri: 'https://zos.alipayobjects.com/rmsportal/gjpzzcrPMkhfEqgbYvmN.svg' }}
                        title="My"
                        key="my"
                        selected={this.state.selectedTab === 'yellowTab'}
                        onPress={() => {
                            this.setState({
                                selectedTab: 'yellowTab',
                            });
                        }}
                    >
                        <AccountPage/>
                    </TabBar.Item>
                </TabBar>
            </div>
        );
    }

}

const condition = authUser => !!authUser;
export default withAuthorization(condition)(HomePage);
