import PasswordChangeForm from '../PasswordChange';
import { AuthUserContext, withAuthorization } from '../Session';//import authorization
import {Flex,List}from 'antd-mobile'

import React, {Component} from 'react';
import './index.css'
class AccountPage extends Component {
    state = {
        avatarUrl:"https://zos.alipayobjects.com/rmsportal/dKbkpPXKfvZzWCM.png",
        userName:"Jack",

    }
    render() {
        const {userName, avatarUrl} = this.state;
        return (
            <AuthUserContext.Consumer>
                {authUser => (
                    <div >
                        <h1>Account: {authUser.email}</h1>
                        <div style={{
                            display:'flex',
                            height:'10vh'
                        }}>

                            <div style={{
                                width:'30%',
                            }} >
                                <img src={avatarUrl} alt="" className={"avatar"}/>

                            </div>
                            <div style={{
                                width:'70%'
                            }} >
                                <span>{userName}</span>

                            </div>


                        </div>

                        <PasswordChangeForm />
                    </div>
                )}
            </AuthUserContext.Consumer>
        );
    }
}


const condition = authUser => !!authUser;  //set condition
export default withAuthorization(condition)(AccountPage);
