import React, { Component } from 'react';

class ImageContainer extends Component {

    render() {
        return (
            <div>
               <div align={"center"}  style={{ borderBottomLeftRadius:'100%', borderBottomRightRadius:'100%',  height:"100%",position:'fixed',width:"100%", top:0,zIndex:2}}
               ></div>
                <img src={this.props.currentImg} style={{width:"70%",height: "80%"}}/>
                <div style={{marginTop: '30px'}}><button onClick={this.props.picClose} >x</button></div>
            </div>

        );
    }
}

export default ImageContainer;
