import Item from '../Item'
class Comments extends Component {
    state={
        comments:[{userName:'Nick',time:'10:30 10/10/2019',content:"abc", likes:5}]
    }

    render() {
        let {comments} = this.state;
        return (
            <div>

            <div>
                <h3 >replyï¼š</h3>
                <h2 style={{display:comments.length>0?'none':'block'}}>no comment</h2>
                <div >
                    {
                        comments.map((item)=>{
                            return (
                                <div><p>{item.userName}</p>
                                    <p>{item.time}</p>
                                    <p>{item.content}</p>
                                    <p>{item.likes}</p></div>)
                        })
                    }

                </div>
            </div>
                <div><a href="#">write a comment</a></div>
            </div>
        );
    }
}

export default Comments;
