import React, { Component } from 'react';

class ImageContainer extends Component {

    render() {
        return (
            <div align={"center"}  style={{backgroundColor: 'lightgrey',  height:"100vh",position:'fixed',width:"100%", top:"50px",zIndex:2}}>

                <img src={this.props.currentImg} style={{width:"70%"}}/>
                <div style={{marginTop: '30px'}}><button onClick={this.props.picClose} >x</button></div>
            </div>

        );
    }
}

export default ImageContainer;
