import React, {Component} from 'react';
import {Icon, ImagePicker, NavBar, WingBlank,Button} from 'antd-mobile';
import * as ROUTES from "../../constants/routes";
import { withFirebase } from '../Firebase';
import {save_image} from '../Firebase/upload'
import { compose } from "recompose";
import { withAuthentication } from "../Session";


const data = [{
    url: 'https://zos.alipayobjects.com/rmsportal/PZUUCKTRIHWiZSY.jpeg',
    id: '2121',
}, ];
class EditAvatar extends Component {
    state = {
        files: data,
        multiple: false,
    }
    onChange = (files, type, index) => {
        console.log(files, type, index);
        this.setState({
            files,
        });
    }
    onClick=()=>{
        let {files} = this.state;

        save_image(this.props.firebase,this.props.firebase.auth.currentUser.uid,files[0].file);

    }

    render() {
        let  files  = {url:this.props.firebase.auth.currentUser.photoURL,id:'211'}

        return (
            <div >
                <NavBar
                    mode="light"
                    icon={<Icon type="left" />}
                    onLeftClick={() => {this.props.history.goBack()}}

                >Update Avatar</NavBar>
                <ImagePicker
                    files={files}
                    onChange={this.onChange}
                    onImageClick={(index, fs) => console.log(index, fs)}
                    selectable={files.length < 1}
                    multiple={this.state.multiple}
                />
                <Button type={"primary"} onClick={this.onClick}>Confirm</Button>
            </div>
        );
    }
}

export default withAuthentication(EditAvatar);
